import React, {Component} from 'react';
import {Col, Row, Button, Modal} from "react-bootstrap";

class Content extends Component {

    constructor(props) {
        super(props);
        this.state = {
            list: [],
            elevenList: [],
            subList: []
        };
       // this.playerList = React.forwardRef();
    }

    pick(pick) {
        console.log(pick);
        if (!this.state.elevenList.find(x => x.id === pick.id) && this.state.elevenList.length < 11) {
            const elevenList = this.state.elevenList;
            elevenList.push(pick);
            this.setState({
                elevenList: elevenList
            });
        }
    }

    componentDidMount() {
        fetch('https://api.scoutium.com/api/clubs/4029/players?count=100')
            .then(response => response.json())
            .then(data => {
                console.log(data);
                this.setState({
                    list: data.players
                });
            });
    }

    delete(player) {
        let elevenList = this.state.elevenList;
        const ind = elevenList.findIndex(x => x.id === player.id);
        elevenList.splice(ind, 1);
        console.log(elevenList);
        this.setState({
            elevenList: elevenList
        });
    }

    addSubList() {
        const [show, setShow] = useState(false);

        const handleClose = () => setShow(false);
        const handleShow = () => setShow(true);
        return (
            <>
                <Button variant="primary" onClick={handleShow}>
                    Launch static backdrop modal
                </Button>

                <Modal
                    show={show}
                    onHide={handleClose}
                    backdrop="static"
                    keyboard={false}
                >
                    <Modal.Header closeButton>
                        <Modal.Title>Modal title</Modal.Title>
                    </Modal.Header>
                    <Modal.Body>
                        I will not close if you click outside me. Don't even try to press
                        escape key.
                    </Modal.Body>
                    <Modal.Footer>
                        <Button variant="secondary" onClick={handleClose}>
                            Close
                        </Button>
                        <Button variant="primary">Understood</Button>
                    </Modal.Footer>
                </Modal>
            </>
        );
    }

    render() {
        const {list, elevenList, subList} = this.state;

        return (
            <main role={"main"} className={"content"}>
                <div className="container">
                    <div className={"box"}>
                            <Row className={"box-one"}>
                                <Col>
                                    <img src={window.location.origin + '/images/teams/bjk-lg@3x.png'} className="d-inline-block align-top" alt="" height="32" /><h1>Beşiktaş JK</h1>
                                </Col>
                                <Col><Button variant="primary">Confirm</Button></Col>
                            </Row>
                            <Row className={"box-two"}>
                                <Col>
                                    <h2>All Players</h2>
                                    <div className="ui list">
                                        {list.map(player =>
                                            <li key={player.id} style={{flex: 1, flexDirection: 'row'}}>
                                                <span>{player.display_name}</span>
                                                <span onClick={() => this.pick(player)}>x</span>
                                            </li>
                                        )}
                                    </div>
                                </Col>
                                <Col>
                                    <h2>Lineup</h2>
                                    <div className="ui list">
                                        {elevenList.map(player =>
                                            <li key={player.id} style={{flex: 1, flexDirection: 'row'}}>
                                                <span>{player.display_name}</span>
                                                <span onClick={() => this.delete(player)}>x</span>
                                            </li>
                                        )}
                                    </div>
                                </Col>
                                <Col>
                                    <h2>Substitutes</h2>
                                    {subList.map(player =>
                                        <li key={player.id} style={{flex: 1, flexDirection: 'row'}}>
                                            <span>{player.display_name}</span>
                                            <span onClick={() => this.delete(player)}>x</span>
                                        </li>
                                    )}
                                    <a onClick={() => this.addSubList()}>Ekle</a>
                                </Col>
                            </Row>
                    </div>
                </div>
            </main>
        );
    }
}

export default Content;
