import React, {Component} from 'react';
import {Col, Row, Button, Modal} from "react-bootstrap";


const Example = React.createClass({

    getInitialState() {
        return { showModal: false };
    },

    close() {
        this.setState({ showModal: false });
    },

    open() {
        this.setState({ showModal: true });
    },

    render() {

        return (
            <div>
                <p>Click to get the full Modal experience!</p>

                <Button
                    bsStyle="primary"
                    bsSize="large"
                    onClick={this.open}
                >
                    Launch demo modal
                </Button>

                <Modal show={this.state.showModal} onHide={this.close}>
                    <Modal.Header closeButton>
                        <Modal.Title>Modal heading</Modal.Title>
                    </Modal.Header>
                    <Modal.Body>
                        <h4>Text in a modal</h4>
                        <p>Duis mollis, est non commodo luctus, nisi erat porttitor ligula.</p>

                        <h4>Popover in a modal</h4>


                        <hr />

                    </Modal.Body>
                    <Modal.Footer>
                        <Button onClick={this.close}>Close</Button>
                    </Modal.Footer>
                </Modal>
            </div>
        );
    }
});


class Content extends Component {

    constructor(props) {
        super(props);
        this.state = {
            list: [],
            elevenList: [],
            subList: []
        };
       // this.playerList = React.forwardRef();
    }

    pick(pick) {
        console.log(pick);
        if (!this.state.elevenList.find(x => x.id === pick.id) && this.state.elevenList.length < 11) {
            const elevenList = this.state.elevenList;
            elevenList.push(pick);
            this.setState({
                elevenList: elevenList
            });
        }
    }

    componentDidMount() {
        fetch('https://api.scoutium.com/api/clubs/4029/players?count=100')
            .then(response => response.json())
            .then(data => {
                console.log(data);
                this.setState({
                    list: data.players
                });
            });
    }

    delete(player) {
        let elevenList = this.state.elevenList;
        const ind = elevenList.findIndex(x => x.id === player.id);
        elevenList.splice(ind, 1);
        console.log(elevenList);
        this.setState({
            elevenList: elevenList
        });
    }



    render() {
        const {list, elevenList, subList} = this.state;
        const [modalShow, addSubList] = React.useState(false);

        return (
            <main role={"main"} className={"content"}>
                <div className="container">
                    <div className={"box"}>
                            <Row className={"box-one"}>
                                <Col>
                                    <img src={window.location.origin + '/images/teams/bjk-lg@3x.png'} className="d-inline-block align-top" alt="" height="32" /><h1>Beşiktaş JK</h1>
                                </Col>
                                <Col><Button variant="primary">Confirm</Button></Col>
                            </Row>
                            <Row className={"box-two"}>
                                <Col>
                                    <h2>All Players</h2>
                                    <div className="ui list">
                                        {list.map(player =>
                                            <li key={player.id} style={{flex: 1, flexDirection: 'row'}}>
                                                <span>{player.display_name}</span>
                                                <span onClick={() => this.pick(player)}>x</span>
                                            </li>
                                        )}
                                    </div>
                                </Col>
                                <Col>
                                    <h2>Lineup</h2>
                                    <div className="ui list">
                                        {elevenList.map(player =>
                                            <li key={player.id} style={{flex: 1, flexDirection: 'row'}}>
                                                <span>{player.display_name}</span>
                                                <span onClick={() => this.delete(player)}>x</span>
                                            </li>
                                        )}
                                    </div>
                                </Col>
                                <Col>
                                    <h2>Substitutes</h2>
                                    {subList.map(player =>
                                        <li key={player.id} style={{flex: 1, flexDirection: 'row'}}>
                                            <span>{player.display_name}</span>
                                            <span onClick={() => this.delete(player)}>x</span>
                                        </li>
                                    )}

                                    <a onClick={() => addSubList(true)}>Ekle</a>
                                    <Example />
                                </Col>
                            </Row>
                    </div>
                </div>
            </main>
        );
    }
}

export default Content;
