import React, {Component} from 'react';
import {Col, Row, Button, Modal} from "react-bootstrap";
import ModalHeader from "react-bootstrap/ModalHeader";
import ModalTitle from "react-bootstrap/ModalTitle";
import ModalBody from "react-bootstrap/ModalBody";
import ModalFooter from "react-bootstrap/ModalFooter";



class Content extends Component {

    constructor(props) {
        super(props);
        this.state = {
            list: [],
            elevenList: [],
            subList: [],
            newList: [],
            showModal: false,
            girenOyuncu: '',
            cikanOyuncu: '',
            dakika: '',
        };
        this.handleSubmit = this.handleSubmit.bind(this);
        this.cikanOyuncuChangeHandler = this.cikanOyuncuChangeHandler.bind(this);
        this.girenOyuncuChangeHandler = this.girenOyuncuChangeHandler.bind(this);
        this.dakikaChangeHandler = this.dakikaChangeHandler.bind(this);
    }

    cikanOyuncuChangeHandler(event) {
        setTimeout(() => {
            console.log(event);
            this.setState({
                cikanOyuncu: event.target.value
            })
        }, 50);

    }

    girenOyuncuChangeHandler(event) {
        setTimeout(() => {
            console.log(event);
            this.setState({
                girenOyuncu: event.target.value
            });
        }, 50);
    }

    dakikaChangeHandler(event) {
        setTimeout(() => {
            console.log(event);
            this.setState({
                dakika: event.target.value
            });
        }, 50);
    }

    pick(pick) {
        console.log(pick);
        const elevenList = this.state.elevenList;
        if (!elevenList.find(x => x.id === pick.id) && elevenList.length < 11) {
            elevenList.push(pick);
            this.setState({
                elevenList: elevenList
            });
        }
    }

    handleSubmit(event) {
        console.log(event);
        event.preventDefault();
        const newList = this.state.newList;
        const girenOyuncu = this.state.girenOyuncu;
        if (!newList.some(x => x.id !== girenOyuncu.id) && newList.length < 3) {
            newList.push(Object.assign({}, girenOyuncu, {dakika: this.state.dakika}));
            this.setState({
                newList: newList
            });
        }
    }

    componentDidMount() {

        const spinner = document.getElementById("spinner");


        spinner.removeAttribute('hidden');
        fetch('https://api.scoutium.com/api/clubs/4029/players?count=100')
            .then(response => response.json())
            .then(data => {
                spinner.setAttribute('hidden', '');
                console.log(data);
                this.setState({
                    list: data.players
                });
            });
    }

    delete(player) {
        let elevenList = this.state.elevenList;
        const ind = elevenList.findIndex(x => x.id === player.id);
        elevenList.splice(ind, 1);
        console.log(elevenList);
        this.setState({
            elevenList: elevenList
        });
    }

    render() {
        const {list, elevenList, subList, cikanOyuncu, girenOyuncu, dakika} = this.state;

        const hideText = document.getElementsByClassName('add-pick')
        const y = document.getElementById("emptyInfo")

        hideText.onclick = () => {
            emptyInfo.style.display = 'none';
        };

        return (
            <main role={"main"} className={"content"}>
                <div className="container">
                    <div className={"box"}>
                        <Row className={"box-one"}>
                            <Col sm={1} md={6}>
                                <img src={window.location.origin + '/images/teams/bjk-lg@3x.png'}
                                     className="d-inline-block align-top" alt="" height="32"/><h1>Beşiktaş JK</h1>
                            </Col>
                            <Col sm={1} md={6}><Button variant="secondary" disabled>Confirm</Button></Col>
                        </Row>
                        <Row className={"box-two"}>
                            <Col xs={12} md={4}>
                                <div className={"inner-cont"}>
                                    <h2>All Players</h2>
                                    <div hidden id="spinner"></div>
                                    <div className="list">
                                        <ul>
                                            {list
                                                .filter(x => !elevenList.find(y => y.id === x.id))
                                                .map(player =>
                                                        <li key={player.id}>
                                                            <span><img src={player.image_url} alt="" width={34} height={34}/></span>
                                                            <span>{player.display_name}
                                                                <em>Forward</em>
                                                </span>
                                                            <span onClick={() => this.pick(player)} className={"add-pick"}>PICK</span>
                                                        </li>
                                                )}
                                        </ul>
                                    </div>
                                </div>
                            </Col>
                            <Col xs={12} md={4}>
                                <div className={"inner-cont"}>
                                    <h2>Lineup</h2>
                                    <div id={"emptyInfo"}><div>You haven’t selected any player for lineup yet.</div></div>
                                    <div className="ui list">
                                        {elevenList
                                            .map(player =>
                                                <li key={player.id} style={{flex: 1, flexDirection: 'row'}}>
                                                    <span>{player.display_name}</span>
                                                    <span onClick={() => this.delete(player)}>x</span>
                                                </li>
                                            )}
                                    </div>
                                </div>
                            </Col>
                            <Col xs={12} md={4}>
                                <div className={"inner-cont"}>
                                    <h2>Substitutes</h2>
                                    {subList.map(player =>
                                        <li key={player.id} style={{flex: 1, flexDirection: 'row'}}>
                                            <span>{player.display_name}</span>
                                            <span onClick={() => this.delete(player)}>x</span>
                                        </li>
                                    )}

                                    {this.state.elevenList.length === 2 ? <a href="#a" onClick={() => this.setState({
                                        showModal: true
                                    })}>Ekle</a> : null}

                                    <Modal show={this.state.showModal} onHide={() => this.setState({showModal: false})}>
                                        <ModalHeader>
                                            <ModalTitle>Hi</ModalTitle>
                                        </ModalHeader>
                                        <ModalBody>
                                            <form onSubmit={this.handleSubmit}>
                                                <select value={cikanOyuncu}
                                                        onChange={this.cikanOyuncuChangeHandler}>
                                                    <option value={''}>Seciniz</option>
                                                    {
                                                        elevenList.map(item =>
                                                            <option key={item.id} value={item}>{item.display_name}</option>
                                                        )
                                                    }
                                                </select>

                                                <select value={girenOyuncu}
                                                        onChange={this.girenOyuncuChangeHandler}>
                                                    <option value={''}>Seciniz</option>
                                                    {
                                                        list.filter(x => !elevenList.find(y => y.id === x.id))
                                                            .map(item =>
                                                                <option key={item.id}
                                                                        value={item}>{item.display_name}</option>
                                                            )
                                                    }
                                                </select>
                                                {/*<Form.Group controlId="exampleForm.ControlSelect1">
                                                <Form.Label>Cikan Oyuncu</Form.Label>
                                                <Form.Control value={cikanOyuncu}
                                                              onChange={(event) => this.cikanOyuncuChangeHandler(event)}>
                                                    <option value={''}>Seciniz</option>
                                                    {
                                                        elevenList.map(item =>
                                                            <option key={item.id} value={item}>{item.display_name}</option>
                                                        )
                                                    }
                                                </Form.Control>
                                            </Form.Group>

                                            <Form.Group controlId="exampleForm.ControlSelect1">
                                                <Form.Label>Giren oyuncu</Form.Label>
                                                <Form.Control value={girenOyuncu}
                                                              onChange={(event) => this.girenOyuncuChangeHandler(event) }>
                                                    <option value={''}>Seciniz</option>
                                                    {
                                                        list.filter(x => !elevenList.find(y => y.id === x.id))
                                                            .map(item =>
                                                                <option key={item.id} value={item}>{item.display_name}</option>
                                                            )
                                                    }
                                                </Form.Control>
                                            </Form.Group>*/}

                                                <input type="text" value={this.state.dakika}
                                                       onChange={this.dakikaChangeHandler}/>

                                            </form>
                                        </ModalBody>
                                        <ModalFooter>This is the footer</ModalFooter>
                                    </Modal>
                                </div>
                            </Col>
                        </Row>
                    </div>
                </div>
            </main>
        );
    }
}

export default Content;
