import React,{ Component } from 'react';
import { connect } from 'react-redux';
import { fetchPlayerList } from "../actions";

class MainList extends  Component {

    constructor(props) {
       super(props);



        this.setState({
            list: []
        });
    }

    componentDidMount() {
        fetch('https://api.scoutium.com/api/clubs/4029/players?count=100').then(data => {
            this.setState({
                list: data.players
            });
        });
    }

   /* findUsername (userId) {
        const { userList } = this.props;
        if  (userList.length > 0 ) {
            return userList.find(user => user.id === userId).name;
        } else {
            return '';
        }
    }*/

    renderItem(post) {
        return (
            <div className="item" key={post.id}>
                <div className="content">
                    <div className="header">{post.title}</div> Post Title <br/> <br/>
                    <div className="description">{post.body}</div> Post desc <br/> <br/>
                </div>


            </div>
        )
    }

    render () {
        return (
            <div className="ui list">
                {this.props.list.map(post => this.renderItem(post))}
            </div>
        )
    }
}

const mapStateToProps = (state) => {
    console.log(state.playerList);
    //console.log(state.userList);

};

export default connect(mapStateToProps, {fetchPlayerList})(MainList);
