import React, {Component} from 'react';
import {Col, Row, Button, Modal, Form} from "react-bootstrap";
import ModalHeader from "react-bootstrap/ModalHeader";
import ModalTitle from "react-bootstrap/ModalTitle";
import ModalBody from "react-bootstrap/ModalBody";
import ModalFooter from "react-bootstrap/ModalFooter";
import FormControl from "react-bootstrap/FormControl";
import InputGroup from "react-bootstrap/InputGroup";


class Content extends Component {

    constructor(props) {
        super(props);
        this.state = {
            list: [],
            elevenList: [],
            subList: [],
            newList: [],
            showModal: false,
            girenOyuncu: null,
            cikanOyuncu: null,
            dakika: null,
        };
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    pick(pick) {
        console.log(pick);
        const elevenList = this.state.elevenList;
        if (!elevenList.find(x => x.id === pick.id) && elevenList.length < 11) {
            elevenList.push(pick);
            this.setState({
                elevenList: elevenList
            });
        }
    }

    handleSubmit(event) {
        const newList = this.state.newList;
        if (!newList.some(x => x.id !== event.value.id) && newList.length < 3) {
            newList.push(event.value);
            this.setState({
                newList: newList
            });
        }
        event.preventDefault();
    }

    componentDidMount() {
        fetch('https://api.scoutium.com/api/clubs/4029/players?count=100')
            .then(response => response.json())
            .then(data => {
                console.log(data);
                this.setState({
                    list: data.players
                });
            });
    }

    delete(player) {
        let elevenList = this.state.elevenList;
        const ind = elevenList.findIndex(x => x.id === player.id);
        elevenList.splice(ind, 1);
        console.log(elevenList);
        this.setState({
            elevenList: elevenList
        });
    }

    render() {
        const {list, elevenList, subList, cikanOyuncu, girenOyuncu, dakika} = this.state;

        return (
            <main role={"main"} className={"content"}>
                <div className="container">
                    <div className={"box"}>
                        <Row className={"box-one"}>
                            <Col>
                                <img src={window.location.origin + '/images/teams/bjk-lg@3x.png'}
                                     className="d-inline-block align-top" alt="" height="32"/><h1>Beşiktaş JK</h1>
                            </Col>
                            <Col><Button variant="primary">Confirm</Button></Col>
                        </Row>
                        <Row className={"box-two"}>
                            <Col>
                                <h2>All Players</h2>
                                <div className="ui list">
                                    {list
                                        .filter(x => !elevenList.find(y => y.id === x.id))
                                        .map(player =>
                                            <li key={player.id} style={{flex: 1, flexDirection: 'row'}}>
                                                <span>{player.display_name}</span>
                                                <span onClick={() => this.pick(player)}>x</span>
                                            </li>
                                        )}
                                </div>
                            </Col>
                            <Col>
                                <h2>Lineup</h2>
                                <div className="ui list">
                                    {elevenList
                                        .map(player =>
                                            <li key={player.id} style={{flex: 1, flexDirection: 'row'}}>
                                                <span>{player.display_name}</span>
                                                <span onClick={() => this.delete(player)}>x</span>
                                            </li>
                                        )}
                                </div>
                            </Col>
                            <Col>
                                <h2>Substitutes</h2>
                                {subList.map(player =>
                                    <li key={player.id} style={{flex: 1, flexDirection: 'row'}}>
                                        <span>{player.display_name}</span>
                                        <span onClick={() => this.delete(player)}>x</span>
                                    </li>
                                )}

                                {this.state.elevenList.length === 11 ? <a onClick={() => this.setState({
                                    showModal: true
                                })}>Ekle</a> : null}

                                <Modal show={this.state.showModal} onHide={() => this.setState({showModal: false})}>
                                    <ModalHeader>
                                        <ModalTitle>Hi</ModalTitle>
                                    </ModalHeader>
                                    <ModalBody>
                                        <form onSubmit={this.handleSubmit}>
                                            <Form.Group controlId="exampleForm.ControlSelect1">
                                                <Form.Label>Cikan Oyuncu</Form.Label>
                                                <Form.Control as="select" value={cikanOyuncu}>
                                                    {
                                                        elevenList.map(item =>
                                                            <option key={item.id} value={item}>{item.display_name}</option>
                                                        )
                                                    }
                                                </Form.Control>
                                            </Form.Group>

                                            <Form.Group controlId="exampleForm.ControlSelect1">
                                                <Form.Label>Giren oyuncu</Form.Label>
                                                <Form.Control as="select" value={girenOyuncu}>
                                                    {
                                                        list.filter(x => !elevenList.find(y => y.id === x.id))
                                                            .map(item =>
                                                                <option key={item.id} value={item}>{item.display_name}</option>
                                                            )
                                                    }
                                                </Form.Control>
                                            </Form.Group>

                                            <InputGroup className="mb-3">

                                                <FormControl
                                                    type={'number'}
                                                    value={dakika}
                                                    aria-label="Default"
                                                    aria-describedby="inputGroup-sizing-default"
                                                />
                                            </InputGroup>
                                        </form>
                                    </ModalBody>
                                    <ModalFooter>This is the footer</ModalFooter>
                                </Modal>
                            </Col>
                        </Row>
                    </div>
                </div>
            </main>
        );
    }
}

export default Content;
