import React, {Component} from 'react';
import {Col, Row, Button, Modal} from "react-bootstrap";
import ModalHeader from "react-bootstrap/ModalHeader";
import ModalTitle from "react-bootstrap/ModalTitle";
import ModalBody from "react-bootstrap/ModalBody";
import ModalFooter from "react-bootstrap/ModalFooter";


class Content extends Component {

    constructor(props) {
        super(props);

        this.state = {
            list: [],
            elevenList: [],
            subList: [],
            newList: [],
            showModal: false,
            girenOyuncu: '',
            cikanOyuncu: '',
            dakika: '',
        };
        this.handleSubmit = this.handleSubmit.bind(this);
        this.cikanOyuncuChangeHandler = this.cikanOyuncuChangeHandler.bind(this);
        this.girenOyuncuChangeHandler = this.girenOyuncuChangeHandler.bind(this);
        this.dakikaChangeHandler = this.dakikaChangeHandler.bind(this);
    }

    cikanOyuncuChangeHandler = (event) => {
        console.log(event);
        event.persist();
        this.setState({
            cikanOyuncu: JSON.parse(event.target.value)
        })
    }

    girenOyuncuChangeHandler = (event) => {
        event.persist();
        this.setState({
            girenOyuncu: JSON.parse(event.target.value)
        });
    }

    dakikaChangeHandler = (event) => {
        event.persist();
        this.setState({
            dakika: event.target.value
        });
    }

    pick(pick) {
        const elevenList = this.state.elevenList;
        const y = document.getElementById("emptyInfo");
        y.style.display = 'none';
        if (!elevenList.find(x => x.id === pick.id) && elevenList.length < 11) {
            elevenList.push(pick);
            this.setState({
                elevenList: elevenList
            });
        }
    }

    handleSubmit(event) {
        event.preventDefault();

        const newList = this.state.newList;
        const girenOyuncu = this.state.girenOyuncu;
        console.log(girenOyuncu, newList);

        if (!newList.some(x => x.id === girenOyuncu.id) && newList.length < 3) {
            newList.push(Object.assign({}, girenOyuncu, {dakika: this.state.dakika}));


            this.setState({
                newList: newList,
                showModal: false
            });
        }
    }

    componentDidMount() {
        const spinner = document.getElementById("spinner");

        spinner.removeAttribute('hidden');
        fetch('https://api.scoutium.com/api/clubs/4029/players?count=100')
            .then(response => response.json())
            .then(data => {
                spinner.setAttribute('hidden', '');
                console.log(data.players[0].position);
                this.setState({
                    list: data.players
                });
            });
    }

    delete(player) {
        let elevenList = this.state.elevenList;
        const ind = elevenList.findIndex(x => x.id === player.id);
        elevenList.splice(ind, 1);
        console.log(elevenList);
        this.setState({
            elevenList: elevenList
        });
    }

    deleteNewListItem(player) {
        let newList = this.state.newList;
        const ind = newList.findIndex(x => x.id === player.id);
        newList.splice(ind, 1);
        this.setState({
            newList: newList
        });
    }

    render() {
        const {list, elevenList, newList} = this.state;


        return (
            <main role={"main"} className={"content"}>
                <div className="container">
                    <div className={"box"}>
                        <Row className={"box-one"}>
                            <Col sm={1} md={6}>
                                <img src={window.location.origin + '/images/teams/bjk-lg@3x.png'}
                                     className="d-inline-block align-top" alt="" height="32"/><h1>Beşiktaş JK</h1>
                            </Col>
                            <Col sm={1} md={6}><Button variant="secondary" disabled>Confirm</Button></Col>
                        </Row>
                        <Row className={"box-two"}>
                            <Col xs={12} md={4}>
                                <div className={"inner-cont"}>
                                    <h2>All Players</h2>
                                    <div hidden id="spinner"></div>
                                    <div className="list">
                                        <ul>
                                            {list
                                                .filter(x => !elevenList.find(y => y.id === x.id))
                                                .filter(x => !newList.find(y => y.id === x.id))
                                                .map(player =>
                                                    <li key={player.id}>
                                                        <span><img src={player.image_url} alt="" width={34}
                                                                   height={34}/></span>
                                                        <span>{player.display_name}
                                                            <em>{player.display_name}</em>
                                                            </span>
                                                        <span onClick={() => this.pick(player)}
                                                              id={"addPick"}>PICK</span>
                                                    </li>
                                                )}
                                        </ul>
                                    </div>
                                </div>
                            </Col>
                            <Col xs={12} md={4}>
                                <div className={"inner-cont"}>
                                    <h2>Lineup</h2>
                                    <div id={"emptyInfo"}>
                                        <div>You haven’t selected any player for lineup yet.</div>
                                    </div>
                                    <div className="list">
                                        <ul>
                                            {elevenList
                                                .map(player =>
                                                    <li key={player.id}>
                                                        <span><img src={player.image_url} alt="" width={34}
                                                                   height={34}/></span>
                                                        <span>{player.display_name}
                                                            <em>Forward</em>
                                                    </span>

                                                        <span onClick={() => this.delete(player)}>x</span>
                                                    </li>
                                                )}
                                        </ul>
                                    </div>
                                </div>
                            </Col>
                            <Col xs={12} md={4}>
                                <div className={"inner-cont"}>
                                    <h2>Substitutes</h2>
                                    {this.state.elevenList.length === 0 ? <div id={"emptyInfo2"}>
                                        <div>Please pick 11 players for lineup before creating any substitutions</div>
