                                                        list
                                                            .filter(x => !elevenList.find(y => y.id === x.id))
                                                            .filter(x => !newList.find(y => y.id === x.id))
                                                            .map(item =>
                                                                <option key={item.id}
                                                                        value={JSON.stringify(item)}>{item.display_name}</option>
                                                            )
                                                    }
                                                </select>
